import React, {useState} from 'react';
import "./Home.css";
import "./css/materialize.css"
class Home extends React.Component {
    state = { isLoading: true, items: [], error: null};
    getFetchitems() { this.setState({ loading: true }, () => {
            fetch("http://localhost:8983/solr/avisos/select?&defType=lucene&indent=false&multiValued=true&q=description:casa").then(res => res.json()).then(result => this.setState({
                loading: false,
                items: result.response.docs
            })).catch(console.log);
        });
    }
    componentDidMount() { this.getFetchitems();}
    render() {
        const { items, error } = this.state;
        let slideIndex = 1;
  showSlides(slideIndex);

  // Next/previous controls
  function plusSlides(n) {
    showSlides(slideIndex += n);
  }

  // Thumbnail image controls
  function currentSlide(n) {
    showSlides(slideIndex = n);
  }

  function showSlides(n) {
    let i;
    let slides = document.getElementsByClassName("mySlides");
    let dots = document.getElementsByClassName("dot");
    if (n > slides.length) {slideIndex = 1}
    if (n < 1) {slideIndex = slides.length}
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[slideIndex-1].style.display = "block";
    dots[slideIndex-1].className += " active";
  }
        return (
            <React.Fragment>
                <h1>All item</h1>
                {
                    error ? <p>
                        {error.message} </p> : null}  {
                    items.map(item => {
                        const {
                            title,
                            id,
                            description,
                            direction,
                            city,
                            province,
                            image,
                            image2,
                            seller
                        } = item;
                        return (
                            <div class="col s2">
                            <div class="product large" key={id} className="product">
                                <div class="product-image">
                                  <div class="slideshow-container">
                                    <div class="mySlides fade">
                                    <img width="128px" height="300px" src={image} onError={(e) => (e.target.onerror = null, e.target.src = "./images/default.jpg")} />
                                    </div>

                                    <div class="mySlides fade">
                                      <img width="128px" height="300px" src={image2} onError={(e) => (e.target.onerror = null, e.target.src = "./images/default.jpg")} />
                                    </div>
                                    <a class="prev" onclick={plusSlides(-1)}>&#10094;</a>
                                    <a class="next" onclick={plusSlides(1)}>&#10095;</a>
                                  </div>
                                    {/* <img width="128px" height="300px" src={image} onError={(e) => (e.target.onerror = null, e.target.src = "./images/default.jpg")} /> */}
                                </div>
                                <div class="product-content">
                                    <span class="text-blue"><a className="name">{title}</a></span>
                                    <p>Ciudad: {city}</p>
                                    <p>Direccion: {direction}</p>
                                </div>
                            </div>
                            </div>
                        );
                    })
                } </React.Fragment>);
    }
}
export default Home;
