import React, {useState} from 'react';
import "./Home.css";
import "./css/materialize.css"
class Home extends React.Component {
    state = { isLoading: true, items: [], error: null};
    getFetchitems(query) { this.setState({ loading: true }, () => {
      // var query = "departamento";
            fetch("http://localhost:8983/solr/avisos/select?&defType=lucene&indent=true&q=description:%22%27"+query+"%27%22%0Atitle:%22%27"+query+"%27%22&q.op=OR&rows=100")
            .then(res => res.json()).then(result => this.setState({
                loading: false,
                items: result.response.docs
            })).catch(console.log);
        });
    }
    componentDidMount(query) { this.getFetchitems(query);}
    render() {
        const { items, error } = this.state;
        return (
            <React.Fragment>
                <h1>
                <button onClick={this.componentDidMount("departamento")}>
  Activate Lasers
</button>
                <form class="col s12">
              <div class="input-field">
              {/* <input type="search" id="search" placeholder="Buscar Productos" onKeyDown={(e) => e.keyCode === 13 ? this.componentDidMount(e.target.value) : void(0)}/> */}
                <label class="label-icon" for="search"><i class="material-icons black-text">search</i></label>
              </div>
            </form>
                </h1>
                {
                    error ? <p>
                        {error.message} </p> : null}  {
                    items.map(item => {
                        const {
                            title,
                            id,
                            description,
                            direction,
                            city,
                            province,
                            image,
                            image2,
                            seller
                        } = item;
                        return (
                            <div class="col s2">
                            <div class="product large" key={id} className="product">
                                <div class="product-image">
                                    <img width="128px" height="300px" src={image} onError={(e) => (e.target.onerror = null, e.target.src = "./images/default.jpg")} />
                                </div>
                                <div class="product-content">
                                    <span class="text-blue"><a className="name">{title}</a></span>
                                    <p>Ciudad: {city}</p>
                                    <p>Direccion: {direction}</p>
                                </div>
                            </div>
                            </div>
                        );
                    })
                } </React.Fragment>);
    }
}
export default Home;


