//               .then(res => res.json()).then(result => this.setState({
//                   loading: false,
//                   items: result.response.docs
//               })).catch(console.log);
//           });

async function getProducts(query){
  var query = "casa"
  return await fetch("http://localhost:8983/solr/avisos/select?&defType=lucene&indent=true&q=description:%22%27"+query+"%27%22%0Atitle:%22%27"+query+"%27%22&q.op=OR&rows=100")
    .then(items => response.json()).then(result => this.setState({
      loading: false,
      items: result.response.docs
  })).catch(console.log);
}

function goto(path){
  window.location = window.location.origin + path
}

function showProducts(items){
  return items.map((item) =>
    <div class="col s2">
      <div class="product large" key={item.id} className="product">
        <div class="product-image">
        <img width="128px" height="300px" src={item.image}  onError={(e) => (e.target.onerror = null, e.target.src = "./images/default.jpg")}/>
        </div>
        <div class="product-content">
          <span class="text-blue"><a className="name">{item.title}</a></span>
          <p>Cantidad disponible: {item.seller}</p>
        </div>
      </div>
    </div>
 )
}


function Home() {
  const [items, setProducts] = useState([])

  if (items.length <= 0){
    getProducts().then(items => {setProducts(items)})
  }

  return (
    <div className="home">
      <nav class=" yellow accent-2 ">
        <div class="nav-wrapper">
            <form class="col s12">
              <div class="input-field">
              </div>
            </form>
          <a href="/" class="brand-logo center blue-text text-darken-2"><img src={logo} width="50px" height="70px" /></a> 
          <ul id="nav-mobile" class="right hide-on-med-and-down">
          </ul>
        </div>
      </nav>
      <div class="row" id="main">
        {showProducts(items)}
      </div>

      <div id="mySidenav" className="sidenav">

        </div>
    </div>
  );
}

export default Home;
