import "./Home.css";
import "./css/materialize.css"
import logo from "./images/home.svg"
// import cart from "./images/cart.svg"
import Cookies from "universal-cookie";

const Cookie = new Cookies();

class Home extends React.Component {
  state = {
      isLoading: true,
      items: [],
      error: null
  };
  getFetchItems() {
      this.setState({
          loading: true
      }, () => {
          fetch("http://localhost:8983/solr/avisos/select?&defType=lucene&indent=false&multiValued=true&q=description:casa").then(res => res.json()).then(result => this.setState({
              loading: false,
              items: result.response.docs
          })).catch(console.log);
      });
  }
  componentDidMount() {
      this.getFetchUsers();
  }
  render() {
      const {
          items,
          error
      } = this.state;
      return (
        <React.Fragment>
        {
              error ? <p>
        {
                  error.message
              } </p> : null}  {
                  items.map(item => {
                      const {
                          title,
                          id,
                          description,
                          direction,
                          city,
                          province,
                          image,
                          image2,
                          seller
                      } = item;
                      return (
                        <div class="col s2">
                        <div class="product large" key={id} className="product">
                          <div class="product-image">
                          <img width="128px" height="300px" src={image}  onError={(e) => (e.target.onerror = null, e.target.src = "./images/default.jpg")}/>
                          </div>
                          <div class="product-content">
                            <span class="text-blue"><a className="name">{title}</a></span>
                            <p>Ciudad: {city}</p>
                            <p>Direccion: {direction}</p>
                          </div>
                          <div class="product-action">
                            <a class="waves-effect waves-light btn yellow black-text" onClick={() => addToCart(product.product_id, setCartItems)}>Agregar al carrito</a>
                          </div>
                        </div>
                      </div>
                      );
                  })
              } </React.Fragment> );
      }
  }
export default Home;
